mapper.select:
select   distinct station  from PP_EQUIPMENT_NUMBER
where  qualified=0

    and station in (${stations})



    and date >= date_format(#{startDate} ,'%Y-%m-%d')


    and date <= date_format(#{endDate} ,'%Y-%m-%d')


    and work_shop = #{workShop}


    and production_line = #{productionLine}


    and shift = #{shift}

select dat.station,dat.date,dat.production_line,dat.work_shop,dat.good_product,dat.workTime ,54 jp, round(good_product*5400/workTime,4) oee,dat.shift from (select t.* , TIME_TO_SEC(w.end_date)-TIME_TO_SEC(w.start_date)+TIME_TO_SEC(r.start_date)-TIME_TO_SEC(r.end_date) workTime from (SELECT count( * ) good_product, work_shop, production_line, station, shift, date FROM PP_EQUIPMENT_NUMBER where qualified=0
and station in (${stations})
and date >= date_format(#{startDate} ,'%Y-%m-%d')
and date <= date_format(#{endDate} ,'%Y-%m-%d')
and date = date_format(#{date} ,'%Y-%m-%d')
and work_shop = #{workShop}
and production_line = #{productionLine}
and shift = #{shift}
GROUP BY date, work_shop, production_line, shift, station order by date )t left join
pp_rest_time r on t.shift=r.shift left join pp_work_time w on t.shift =w.shift) dat
